function existAudio() {
  const audio = document.querySelector('audio');
  if (audio) {
    audio.remove();
  }
}

function renderingAudio(obj) {
  let blobUrl = obj.audio.dataURL;
  window.audio = new Audio();
  window.audio.src = blobUrl;
  document.body.appendChild(window.audio);
  document.querySelector('#button-audio').addEventListener('click', () => {
    window.audio.play();
  })
}

function renderingPhrasesList(selector, obj) {
  let contain = document.querySelector(`#${selector}`);
  contain.innerHTML = '';
  for (key in obj) {
    if (obj[key] !== null) {
      contain.innerHTML += `
        <li class="phrase">${obj[key]}</li>
      `;
    }
  }
}

function renderingPhrasesRussian(obj) {
  let pharseRu = obj.context.phrase.hTranslations;
  renderingPhrasesList('phrase-ru', pharseRu);
}

function renderingPhrasesEnglish(obj) {
  let phraseEn = obj.context.phrase.subtitles;
  renderingPhrasesList('phrase-en', phraseEn);
}

function renderingEnglishFront(obj) {
  let wordEn = obj.word.text,
    front = document.querySelector('#front');  

  front.innerHTML = `<p class="word">${wordEn}</p>`;
  initSearch(wordEn);
}

function renderingTranslateList(obj) {
  let wordsRu = obj.wordTranslationsArr,
    list = document.querySelector('#translate-list');

  list.innerHTML = '';
  wordsRu.forEach(wordRu => {
    list.innerHTML += `
      <li class="translate">${wordRu}</li>
    `;
  })
}

function renderingFlipCard(obj) {
  renderingEnglishFront(obj);
  renderingTranslateList(obj);
  renderingPhrasesEnglish(obj);
  renderingPhrasesRussian(obj);
  existAudio();
  renderingAudio(obj);
}

function toggleFlipping() {
  document.querySelector('#front').classList.toggle('active');
  document.querySelector('#back').classList.toggle('active');
}

function changesSide() {
  document.querySelector('#container').addEventListener('click', () => {
    toggleFlipping();
  })
}

function eventsNull() {
  document.querySelector('#button-comback').onclick = null;
  document.querySelector('#button-continue').onclick = null;
  document.onkeydown = null;
}

function chunkArray(array, chunkSize) {
  let result = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    result.push(array.slice(i, i + chunkSize));
  }
  return result;
}

function renderWordGroups(data) {
  return new Promise((resolve) => {
    MicroModal.show('modal-range');
    
    let chunks = chunkArray(data, 40);

    document.querySelectorAll('[data-range]').forEach(button => {
      button.addEventListener('click', function () {
        let rangeId = this.dataset.range;
        MicroModal.close('modal-range');
        resolve(chunks[rangeId]); // return data by clicking
      }, { once: true }); // this event works only once
    });
  });
}

async function initApp(data, value) {
  eventsNull();

  if (value === 'w0') {
    data = await renderWordGroups(data);
  }
  
  for (let i = 0; i < data.length; i++) {
    renderingFlipCard(data[i]);
    document.querySelector('#button-comback').onclick = function () {
      data.unshift(data.pop());
      renderingFlipCard(data[i]);
    }
    document.querySelector('#button-continue').onclick = function () {
      data.push(data.shift());
      renderingFlipCard(data[i]);
    }
    document.onkeydown = function (event) {      
      if (event.code === 'ArrowLeft') {
        data.unshift(data.pop());
        renderingFlipCard(data[i]);
      }
      if (event.code === 'ArrowRight') {
        data.push(data.shift());
        renderingFlipCard(data[i]);
      }
      if (event.code === 'Space') {
        toggleFlipping();
      }
      if (event.code === 'ArrowDown') {
        window.audio.play();
      }
    }
    break;
  }
}

function initFetch(value) {
  let url = `json/${value}.json`;
  fetch(url)
    .then((response) => response.json())
    .then((data) => {
      initApp(data, value);
    })
}

(function init() {
  const defaultUrl = 'w0';
  const wordList = document.querySelectorAll('[data-words-list]');

  for (let list of wordList) {
    list.addEventListener('click', function () {
      toggleMarker(this, wordList);
      initFetch(this.dataset.wordsList);
      MicroModal.close('modal-list');
    })
  }
  changesSide();
  initFetch(defaultUrl);
}())
MicroModal.init();
const modalButton = document.querySelector('[data-modal]');
modalButton.addEventListener('click', function() {
  MicroModal.show(this.dataset.modal);  
})
function renderingInterfaceSearch(obj) {
  const content = document.querySelectorAll('[data-search-content]');
  for (let block of content) {
    block.innerHTML = '';
    if (block.dataset.searchContent === 'phonetics') {
      obj.phonetics.forEach(el => {
        if (el.text !== undefined) {
          block.innerHTML += `<p class="sentences__el">${el.text}</p>`;
        }
      })
    }
    if (block.dataset.searchContent === 'synonyms') {
      for (let i = 0; i < obj.meanings.length; i++) {
        obj.meanings[i].synonyms.forEach(el => {
          block.innerHTML += `<li class="sense__el">${el}</li>`;
        })
      }
    }
    if (block.dataset.searchContent === 'antonyms') {
      for (let i = 0; i < obj.meanings.length; i++) {
        obj.meanings[i].antonyms.forEach(el => {
          block.innerHTML += `<li class="sense__el">${el}</li>`;
        })
      }
    }
    if (block.dataset.searchContent === 'definitions') {
      for (let i = 0; i < obj.meanings.length; i++) {
        obj.meanings[i].definitions.forEach(el => {
          block.innerHTML += `<p class="sentences__el">${el.definition}</p>`;
        })
      }
    }
  }
  document.querySelector('[data-search-source]').onclick = function () {
    obj.sourceUrls.forEach(el => {
      window.open(el);
    })
  }
}

function initSearch(word) {
  document.querySelector('[data-search]').onclick = null;
  document.querySelector('[data-search]').onclick = function () {
    const urlSearch = `https://api.dictionaryapi.dev/api/v2/entries/en/${word}`;
    fetch(urlSearch)
      .then((response) => response.json())
      .then((data) => {
        renderingInterfaceSearch(data[0]);
        MicroModal.show('modal-search');
      })
  }
}
function toggleMarker(current, array) {
  array.forEach(el => {
    el.classList.remove('active-list');
  })
  current.classList.add('active-list');
}